import express, { type Express } from "express";
import fs from "fs";
import path from "path";
import { createServer as createViteServer, createLogger } from "vite";
import { type Server } from "http";
import viteConfig from "../vite.config";
import { nanoid } from "nanoid";

const viteLogger = createLogger();

export function log(message: string, source = "express") {
  const formattedTime = new Date().toLocaleTimeString("en-US", {
    hour: "numeric",
    minute: "2-digit",
    second: "2-digit",
    hour12: true,
  });

  console.log(`${formattedTime} [${source}] ${message}`);
}

export async function setupVite(app: Express, server: Server) {
  const serverOptions = {
    middlewareMode: true,
    hmr: { server },
    allowedHosts: true as const,
  };

  const vite = await createViteServer({
    ...viteConfig,
    configFile: false,
    customLogger: {
      ...viteLogger,
      error: (msg, options) => {
        viteLogger.error(msg, options);
        process.exit(1);
      },
    },
    server: serverOptions,
    appType: "custom",
  });

  app.use(vite.middlewares);
  app.use("*", async (req, res, next) => {
    const url = req.originalUrl;

    try {
      const clientTemplate = path.resolve(
        import.meta.dirname,
        "..",
        "client",
        "index.html",
      );

      // always reload the index.html file from disk incase it changes
      let template = await fs.promises.readFile(clientTemplate, "utf-8");
      template = template.replace(
        `src="/src/main.tsx"`,
        `src="/src/main.tsx?v=${nanoid()}"`,
      );
      const page = await vite.transformIndexHtml(url, template);
      res.status(200).set({ "Content-Type": "text/html" }).end(page);
    } catch (e) {
      vite.ssrFixStacktrace(e as Error);
      next(e);
    }
  });
}

export function serveStatic(app: Express) {
  const distPath = path.resolve(import.meta.dirname, "public");

  if (!fs.existsSync(distPath)) {
    throw new Error(
      `Could not find the build directory: ${distPath}, make sure to build the client first`,
    );
  }

  app.use(express.static(distPath));

  // fall through to index.html if the file doesn't exist
  app.use("*", (_req, res) => {
    res.sendFile(path.resolve(distPath, "index.html"));
  });
}

shared/schema.ts.content:
import { z } from "zod";

export const vulnerabilitySchema = z.object({
  id: z.string(),
  name: z.string(),
  severity: z.enum(["critical", "high", "medium", "low"]),
  status: z.enum(["open", "in-progress", "resolved"]),
  assignedTo: z.string(),
  dueDate: z.string(),
  description: z.string(),
  createdAt: z.string(),
});

export const teamMemberSchema = z.object({
  id: z.string(),
  name: z.string(),
  role: z.string(),
  avatar: z.string(),
  status: z.enum(["online", "offline", "away"]),
  email: z.string(),
});

export const aiMessageSchema = z.object({
  id: z.string(),
  content: z.string(),
  type: z.enum(["user", "ai"]),
  timestamp: z.string(),
});

export const userSchema = z.object({
  id: z.string(),
  username: z.string(),
  password: z.string(),
  name: z.string(),
  avatar: z.string(),
});

export type Vulnerability = z.infer<typeof vulnerabilitySchema>;
export type TeamMember = z.infer<typeof teamMemberSchema>;
export type AIMessage = z.infer<typeof aiMessageSchema>;
export type User = z.infer<typeof userSchema>;

export const insertVulnerabilitySchema = vulnerabilitySchema.omit({ id: true, createdAt: true });
export const insertTeamMemberSchema = teamMemberSchema.omit({ id: true });
export const insertAIMessageSchema = aiMessageSchema.omit({ id: true });
export const insertUserSchema = userSchema.omit({ id: true });

export type InsertVulnerability = z.infer<typeof insertVulnerabilitySchema>;
export type InsertTeamMember = z.infer<typeof insertTeamMemberSchema>;
export type InsertAIMessage = z.infer<typeof insertAIMessageSchema>;
export type InsertUser = z.infer<typeof insertUserSchema>;

